@import url(https://cdn.rawgit.com/tonsky/FiraCode/1.205/distr/fira_code.css);


body {
    margin: auto;
    // font-family: 'Fira Code', sans-serif;
    font-family: 'Quicksand', sans-serif;
}
a {
    color: black;
    font-weight: 800;
}
a:hover {
    color: rgba(0, 0, 0, 0.5);
}

// bootstrapowe taby cardy
section {
    border: 2px solid black;
    border-radius: 5px 5px;
    border-bottom: none;
    .nav {
        background-color: #fff;
        margin:auto;
        margin-top: 120px;
        font-size: 2rem;
        width: 100%;
        display: flex;
        justify-content: center;
        color: black;
        .nav-item {
            color: black;
        }
        .nav-link {
            background-color: rgba(255, 255, 255, 0.301);
        }
        .active {
            border: 2px solid black !important;
            border-bottom: none;
        }
    }
}
nav { 
    
    img {
        width: 100%;
}
.button:hover {
    text-decoration: underline;
}
    .modal {
        margin-top: 50px;
        width: 100%;
    }
    .socialnav {

        background-color: rgb(255, 209, 6);
        font-weight: 800;
        position: fixed;
        width: 100%;
        z-index: 9999;
        display: flex;
        justify-content: flex-end;
        padding: 10px;
        margin-bottom: 150px;
        .title {
            flex: 2;
        }
        .el {
            border-left: 2px solid black;
            margin: 0 15px;
            padding: 0;
            display: flex;
            flex-direction: row;
            align-items: center;
             i {
                 margin-left: 2px;
                 margin-right: 10px;
                 align-self: center;
             }
            .content {
                display: flex;
                flex-direction: column;
                align-self: center;
                p {
                    align-self: center;
                    padding: 0;
                    margin: 0;
                    
                }
                p:hover {
                    text-decoration: underline;
                    cursor: pointer;

                }
                .tel:hover {
                    text-decoration: underline;
                    cursor: pointer;
                }
            }
        }
    }

    ul {
        display: flex;
        flex-direction: row;
        font-size: 2rem;
        opacity: 0.7;
    
         li {
             list-style-type: none;
             margin: 5px;
    
             &:hover {
                 color: red;
                 opacity: 1;
                 cursor: pointer;
             }
         }
    }
}
main {
    position: relative;
    display: flex;
    flex-direction: column;
    margin: auto;
    text-align: center;
    width: 100%;
    height:600px;
    z-index: 222;
    
    .bg {
        background-image: url("https://images.pexels.com/photos/788200/pexels-photo-788200.jpeg?auto=compress&cs=tinysrgb&dpr=3&h=750&w=1260");
        background-size: cover;
        background-repeat: no-repeat;
        object-fit: cover;
        background-position: center;
        position: absolute;
        height: 100%;
        width: 100%;
        z-index: -99;
        opacity: 0.6;
        
    
    }
    .tab-pane {
        
        color: black;
        font-size: 1.7rem;
        padding: 0 150px;
        margin: 150px;
        justify-self: center;
        align-self: center;
    }
   

    .hero {
        background-color: rgba(255, 0, 0, 0.247);
        width: 600px;
        margin: 100px auto;
        display: flex;


        .box {
            border: 2px solid black;
            margin: 10px;
            width: 40px;
            height: 40px;
        }
        .one {
            flex: 2;
        }
        .two {
            flex: 1;
            justify-self: start;
        }
        .three  {
            flex: 1.5;
        }
    }
}
footer {
    border: 2px solid black;
    border-bottom: none;
    display: flex;
    position: sticky;
    margin: auto;
    justify-content: space-evenly;
    ul {
        display: flex;
        flex-direction: column;
    }
}
